t=0
frame_rate = 8
frame = 1
x=96
y=24
run={262,264,266,268} 
map_pos = 0
map_pos_blur = 0
--
default_pal = {}
faded_pal = {}

function BOOT()
	trace("Boot")
end

function TIC()

	if btn(0) then y=y-1 end
	if btn(1) then y=y+1 end
	if btn(2) then 
		--map_pos=map_pos-0.2
		map_pos_blur = map_pos_blur + 1.0
	end
	if btn(3) then 
		--map_pos=map_pos+0.2
		map_pos_blur = map_pos_blur - 1.0
	end
	
	frame_rate = frame_rate - 1
	if frame_rate == 0 then
		frame_rate = 8
		frame = frame + 1
		if frame > 4 then frame = 1 end
		end

	if map_pos_blur >= 8.0 then
		map_pos_blur = 0.0
		map_pos = map_pos - 1
	end
	if map_pos_blur <= -8.0 then
		map_pos_blur = 0.0
		map_pos = map_pos + 1
	end

	cls(8)
	map(map_pos, 0, 32, 17, map_pos_blur, 0, 8, 1, nil)
	spr(run[frame],5,5,0,1,0,0,2,2)
	print("HELLO WORLD!",84,84)
	print("mp: " .. tostring(map_pos), 84, 94)
	print("mpb: " .. tostring(map_pos_blur), 84, 104)
end

