function pset(p)
	for i=0, 11 do
		poke(0x3FC0+i,p[i+1],8)
	end
end

function BOOT()
	trace("BOOT")
	poke4(2 * 0x3ffc, 4)
	cls()
	map()
	for i=0, 240 do
		trace(i..": "..peek4(0x0000 + i))
	end
end

function TIC()
	cls()
	map()
	for x=0, 29 do
		for y=0, 16 do
			local tileid = mget(x,y)
			if tileid == 3
			or tileid == 4
			or tileid == 19
			or tileid == 20 then
				local xpos = x*8
				local ypos = y*8
			end
		end
	end
end

